import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class Angular2PhotoswipeService {

  constructor() { }
}
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'ngp-angular2-photoswipe',
  template: `
    <p>
      angular2-photoswipe works!
    </p>
  `,
  styles: []
})
export class Angular2PhotoswipeComponent implements OnInit {

  constructor() { }

  ngOnInit() {
  }

}
import { Injectable, ElementRef, Inject } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class NgpService {

  LightboxElement: ElementRef;
}
import { Component, ViewChild, ElementRef, AfterContentInit } from '@angular/core';
import { NgpService } from '../ngp.service';

@Component({
  selector: 'ngp-lightbox',
  templateUrl: './lightbox.component.html',
  styleUrls: ['./lightbox.component.scss']
})
export class LightboxComponent implements AfterContentInit {

  @ViewChild('ngpLightbox', {static: true}) el: ElementRef;
  constructor(private ngp: NgpService) {
  }

  ngAfterContentInit() {
    this.ngp.LightboxElement = this.el;
  }

}
export class Image {

  id: number;
  largeUrl: string;
  thumbUrl: string;
  size: string;
  width: number;
  height: number;
  description: string;
  author: string;

  constructor() {}
}
import { LightboxOptions, LightboxMethods, ShareButton, CustomOptions } from './lightbox-options';
import { Injectable } from '@angular/core';

export function ANGULAR2_PHOTO_SWIPE_FACTORY() {
    return new DefaultLightboxAdapter();
}

@Injectable({ providedIn: 'root', useFactory: ANGULAR2_PHOTO_SWIPE_FACTORY })
export abstract class LightboxAdapter implements LightboxOptions, LightboxMethods, CustomOptions {
    getThumbBoundsFn: Function;
    getDoubleTapZoom: Function;
    isClickableElement: Function;
    addCaptionHTMLFn: Function;

    index: number;
    showHideOpacity: boolean;
    showAnimationDuration: number;
    hideAnimationDuration: number;
    bgOpacity: number;
    spacing: number;
    allowPanToNext: boolean;
    maxSpreadZoom: number;
    loop: boolean;
    pinchToClose: boolean;
    closeOnScroll: boolean;
    closeOnVerticalDrag: boolean;
    mouseUsed: boolean;
    escKey: boolean;
    arrowKeys: boolean;
    history: boolean;
    galleryUID: number;
    galleryPIDs: boolean;
    errorMsg: string;
    barsSize: Object;
    timeToIdle: number;
    timeToIdleOutside: number;
    loadingIndicatorDelay: number;
    closeEl: boolean;
    captionEl: boolean;
    fullscreenEl: boolean;
    zoomEl: boolean;
    shareEl: boolean;
    counterEl: boolean;
    arrowEl: boolean;
    preloaderEl: boolean;
    tapToClose: boolean;
    tapToToggleControls: boolean;
    clickToCloseNonZoomable: boolean;
    closeElClasses: string[];
    indexIndicatorSep: string;
    shareButtons: ShareButton[];
    enableBootstrap4: boolean;
}

@Injectable()
export class DefaultLightboxAdapter extends LightboxAdapter {
    allowPanToNext = true;
    spacing = 0.12;
    bgOpacity = 1.0;
    mouseUsed = false;
    loop = true;
    pinchToClose = true;
    closeOnScroll = true;
    closeOnVerticalDrag = true;
    hideAnimationDuration = 333;
    showAnimationDuration = 333;
    showHideOpacity = false;
    escKey = true;
    arrowKeys = true;
    enableBootstrap4: false;
}
import { Component, Input, Output, EventEmitter, HostBinding } from '@angular/core';
import { Image } from '../image';
import { LightboxAdapter } from '../lightbox-adapter';

@Component({
  selector: 'ngp-gallery-item',
  templateUrl: './gallery-item.component.html',
  styleUrls: ['./gallery-item.component.css']
})
export class GalleryItemComponent {

  @Input() image: Image;
  @Output() clicked = new EventEmitter<Image>();
  @HostBinding('class.card') isBootstrapEnabled: boolean = false;

  constructor(private adapter: LightboxAdapter) {
    this.isBootstrapEnabled = this.adapter.enableBootstrap4;
  }

  public onClick() {
    this.clicked.emit(this.image);
    return false;
  }

}
import { Component, ContentChildren, AfterContentInit, QueryList, ViewChild, ElementRef, OnDestroy, EventEmitter, Input, Output } from '@angular/core';
import * as PhotoSwipe from 'photoswipe';
import * as PhotoSwipeUI_Default from 'photoswipe/dist/photoswipe-ui-default';

import { GalleryItemComponent } from '../gallery-item/gallery-item.component';
import { Image } from '../image';
import { NgpService } from '../ngp.service';
import { Subscription } from 'rxjs';

import { LightboxOptions } from '../lightbox-options';
import { LightboxAdapter } from '../lightbox-adapter';

@Component({
  selector: 'ngp-gallery',
  templateUrl: './gallery.component.html',
  styleUrls: ['./gallery.component.css']
})
export class GalleryComponent implements AfterContentInit, OnDestroy {

  @ViewChild('ngpGallery', { static: true }) galleryElement: ElementRef<HTMLDivElement>;
  @ContentChildren(GalleryItemComponent) galleryItems: QueryList<GalleryItemComponent>;
  @Input() galleryId: string;
  @Output() shareLinkClick: EventEmitter<{ e: Event, target: HTMLElement }> = new EventEmitter();

  subscriptions: Subscription[] = [];
  isBootstrapEnabled: boolean;
  pswp: PhotoSwipe;

  images: Image[];

  constructor(private ngp: NgpService, private adapter: LightboxAdapter) {
    this.images = [];
    this.isBootstrapEnabled = this.adapter.enableBootstrap4;
  }

  ngAfterContentInit() {
    this.images = <any>this.galleryItems.toArray().map(cp => {
      // listen for clicks;
      this.subscriptions.push(cp.clicked.subscribe((data) => this.onClick(data)));
      return cp.image;
    });
  }

  ngOnDestroy() {
    this.subscriptions.forEach(sub => sub.unsubscribe());
  }

  onClick(data) {
    this.openPhotoSwipe(data, this.galleryElement);
  }

  private openPhotoSwipe(img: Image, galleryDOM: ElementRef): boolean {
    this.adapter.galleryUID = galleryDOM.nativeElement.getAttribute('data-pswp-uid');
    this.adapter.index = img.id;
    const PSWP: HTMLElement = <HTMLElement>this.ngp.LightboxElement.nativeElement;
    this.pswp = new PhotoSwipe(PSWP, PhotoSwipeUI_Default, this.getImagesAsPhotoswipe(), this.adapter);
    const _this = this;
    this.pswp.listen('shareLinkClick', function (e, target) {
      _this.shareLinkClick.emit({ e, target });
    });
    this.pswp.init();
    return false;
  }

  private getImagesAsPhotoswipe(): any[] {
    return this.images.map(image => {
      return {
        src: image.largeUrl,
        w: image.width,
        h: image.height,
        pid: image.id,
        title: image.description,
        author: image.author
      };
    });

  }
}
import { CommonModule } from '@angular/common';
import { NgModule } from '@angular/core';
import { Angular2PhotoswipeComponent } from './angular2-photoswipe.component';
import { LightboxComponent } from './lightbox/lightbox.component';
import { GalleryComponent } from './gallery/gallery.component';
import { GalleryItemComponent } from './gallery-item/gallery-item.component';
export { LightboxAdapter } from './lightbox-adapter';

@NgModule({
  imports: [CommonModule],
  declarations: [Angular2PhotoswipeComponent, LightboxComponent, GalleryComponent, GalleryItemComponent],
  exports: [LightboxComponent, GalleryComponent, GalleryItemComponent]
})
export class Angular2PhotoswipeModule {
}
